const data = $input.first().json;

let severity = 'INFO';
if (data.leaked_connections > 0) severity = 'CRITICAL';
else if (data.n8n_connections > 25) severity = 'CRITICAL';
else if (data.utilization_pct > 80) severity = 'HIGH';
else if (data.n8n_connections > 15) severity = 'WARNING';

const alert = {
  timestamp: new Date().toISOString(),
  severity: severity,
  service: 'postgres_connection_pool',
