    utilization_pct: data.utilization_pct,
    leaked_connections: data.leaked_connections,
    active: data.active_connections,
    idle: data.idle_connections
  },

  analysis: {
    connections_remaining: data.max_connections - data.total_connections,
    is_leak_detected: data.leaked_connections > 0,
    threshold_exceeded: data.n8n_connections > 15
  },

  recommended_actions: []
};

if (data.leaked_connections > 0) {
  alert.recommended_actions.push(
    'ðŸ”´ CONNECTION LEAK - Check idle transactions',
