// Get content_type from first chunk
const first = $input.first().json;
const content_type = first.content_type || 'episode';

return [{
  json: {
    ...$json,
    content_type,
    is_episode: content_type === 'episode',
    is_book: content_type === 'book',
    is_newsletter: content_type === 'newsletter'
  }
}];

function normalizeEpisodeTitle(candidateTitle, candidateFilename) {
  const pick = s => (s || '').toString();
  const t = pick(candidateTitle);
  const f = pick(candidateFilename);
  const rx = /(?:^|\b)(?:ep(?:isode)?|episode)\s*[:#-]?\s*([0-9]{1,4})/i;
  let m = t.match(rx); if (m) return 'Episode ' + m[1];
  m = f.match(rx);     if (m) return 'Episode ' + m[1];
  return t || 'Episode';
}

const rows = $input.all().map(i => i.json);
if (!rows.length) {
  return [{ json: { error: 'no_chunks_found', file_id: $json.file_id || null } }];
}

rows.sort((a,b) => (a.chunk_index||0) - (b.chunk_index||0));
const content = rows.map(r => String(r.text||'')).join('\n\n');

const first = rows[0] || {};
const file_id  = first.file_id  || null;
const filename = first.filename || '';
const content_type = first.content_type || 'episode';

const episode_title  = normalizeEpisodeTitle(filename, filename);
const numMatch = (episode_title.match(/Episode\s+([0-9]{1,4})/i) || [])[1];
const episode_number = numMatch ? parseInt(numMatch, 10) : null;

return [{
  json: {
    episode_content: content.slice(0, 20000),
    episode_title,
    episode_number,
    file_id,
    filename,
    content_type,
    chunk_count: rows.length
  }
}];

// RANK TOPICS (copy from Workflow 10)
const first = $input.first();
const raw = String((first && first.json && (first.json.output ?? first.json.text)) || '').trim();

let concepts = [];
const stripFences = s => s.replace(/```json|```/g, '').trim();

const isLikelyJSON = (s) => {
  const t = s.trim().slice(0, 1);
  return t === '[' || t === '{';
};

try {
  if (isLikelyJSON(raw)) {
    const clean = stripFences(raw);
    const parsed = JSON.parse(clean);
    concepts = Array.isArray(parsed) ? parsed : (parsed.concepts || []);
  }
} catch (e) {
  throw new Error('Concept JSON parse fail: ' + e.message);
}

const W = { a: 0.35, p: 0.25, r: 0.20, v: 0.15, e: 0.05 };
const pick = (obj, key, fallback) => (obj && obj[key] != null ? obj[key] : fallback);

const scored = concepts.map(c => {
  const actionability = String(pick(c, 'actionability', 'medium'));
  const proofType = String(pick(c, 'proof_type', 'story'));
  const platformFit = String(pick(c, 'platform_fit', 'all'));
  const hookPotential = Number(pick(c, 'hook_potential', 7)) || 7;

  const a = 7 + (actionability === 'high' ? 2 : actionability === 'medium' ? 1 : 0) + (proofType !== 'none' ? 1 : 0);
  const p = platformFit === 'all' ? 10 : 8;
  const r = proofType === 'data' ? 9 : (proofType === 'framework' ? 8 : 7);
  const v = Math.max(1, Math.min(10, hookPotential));
  const en = 7;

  const wt = a * W.a + p * W.p + r * W.r + v * W.v + en * W.e;
  return { 
    ...c, 
    scores: { weighted_total: Math.round(wt * 100) / 100 } 
  };
});

let ep;
try {
  const arr = $items('Combine Episode Content', 0, 0);
  ep = (arr && arr[0] && arr[0].json) || {};
} catch (_) {
  ep = {};
}

const top = scored
  .sort((a, b) => b.scores.weighted_total - a.scores.weighted_total)
  .slice(0, 10);

return top.map((c, i) => ({
  day_number: i + 1,
  ...c,
  episode_title: ep.episode_title || $json?.episode_title || '',
  episode_number: ep.episode_number || $json?.episode_number || '',
  file_id: ep.file_id || $json?.file_id || '',
  content_type: ep.content_type || $json?.content_type || 'episode'
}));

// Parse Generated Content (from agent output)
const readFromInputs = () => {
  const all = $input.all();
  for (const it of all) {
    const j = it.json || {};
    let candidate = j.output || j.text || j.generated_content || j.response_data;

    if (typeof candidate === 'string' && candidate.trim()) {
      return candidate.trim();
    }
  }
  return '';
};

const content = readFromInputs();

if (!content) {
  throw new Error('No generated content found in inputs');
}

// Extract sections
const ig = (content.match(/###\s*INSTAGRAM REEL\s*\n([\s\S]*?)(?=###|$)/i) || [])[1] || '';
const fb = (content.match(/###\s*FACEBOOK POST\s*\n([\s\S]*?)(?=###|$)/i) || [])[1] || '';
const li = (content.match(/###\s*LINKEDIN POST\s*\n([\s\S]*?)(?=###|$)/i) || [])[1] || '';

return [{
  json: {
    ...$json,
    instagram_content: ig.trim(),
    facebook_content: fb.trim(),
    linkedin_content: li.trim(),
    generated_content: content
  }
}];

// Inject CTAs
const day = Number($json.day_number || 1);

const cta = (day <= 5) 
  ? "\n\nðŸ‘‰ Like, comment, and subscribe for more!"
  : "\n\nðŸ‘‰ Join our FREE Skool community: https://www.skool.com/agentelite/about";

const addCTA = (content) => {
  if (!content) return content;
  const hasSkool = content.includes('skool.com');
  const hasSubscribe = content.toLowerCase().includes('subscribe');
  if (hasSkool || hasSubscribe) return content;
  return content + cta;
};

return [{
  json: {
    ...$json,
    instagram_content: addCTA($json.instagram_content),
    facebook_content: addCTA($json.facebook_content),
    linkedin_content: addCTA($json.linkedin_content),
    cta_type: day <= 5 ? 'engagement' : 'skool'
  }
}];

// setPromptVersion for review
const force = String($json.force_version || '').toLowerCase();
let v = null;

if (force === 'v2' || force === 'v3' || force === 'v4') {
  v = force;
} else {
  const status = String($json.review_status || '').toUpperCase();
  const needsStrict = (status === 'REJECT' || status === 'APPROVE_WITH_EDITS' || status === 'CHANGES_REQUESTED');
  v = needsStrict ? 'v2' : 'v4';  // Use v4 by default
}

return [{ json: { ...$json, prompt_key: 'expert_review', prompt_version: v } }];

// Parse Review JSON
if (typeof $json === 'object' && $json.overall_recommendation) {
  return [{ json: $json }];
}

const raw = (typeof $json.output === 'string' && $json.output) || (typeof $json.text === 'string' && $json.text) || '';

if (!raw) {
  return [{
    json: {
      overall_recommendation: "REJECT",
      summary: "No output field found in Expert Reviewer response.",
      action_items: ["Check Expert Reviewer node configuration."]
    }
  }];
}

let obj;
try {
  obj = JSON.parse(raw);
} catch (directError) {
  try {
    const cleaned = raw.replace(/```json|```/g, '').trim();
    obj = JSON.parse(cleaned);
  } catch (fenceError) {
    const s = raw.indexOf('{');
    const e = raw.lastIndexOf('}');
    if (s >= 0 && e > s) {
      try {
        obj = JSON.parse(raw.slice(s, e + 1));
      } catch (extractError) {}
    }
  }
}

if (!obj || !obj.overall_recommendation) {
  obj = {
    overall_recommendation: "REJECT",
    summary: "Reviewer output was not valid JSON",
    action_items: ["Re-run Expert Review with strict JSON output."]
  };
}

return [{ json: obj }];

// Merge Review + Enriched
const reviewData = $input.first().json;
const enrichedData = $('Combine Episode Content').first().json;

return [{
  json: {
    ...enrichedData,
    ...reviewData,
    episode_title: enrichedData.episode_title || reviewData.episode_title,
    file_id: enrichedData.file_id || reviewData.file_id,
    content_type: enrichedData.content_type || reviewData.content_type || 'episode'
  }
}];
