function normalizeEpisodeTitle(candidateTitle, candidateFilename) {
  const pick = s => (s || '').toString();
  const t = pick(candidateTitle);
  const f = pick(candidateFilename);
  const rx = /(?:^|\b)(?:ep(?:isode)?|episode)\s*[:#-]?\s*([0-9]{1,4})/i;
  let m = t.match(rx); if (m) return 'Episode ' + m[1];
  m = f.match(rx);     if (m) return 'Episode ' + m[1];
  return t || 'Episode';
}

// read all chunk rows from this node's input
const rows = $input.all().map(i => i.json);
if (!rows.length) {
  return [{ json: { error: 'no_chunks_found', file_id: $json.file_id || null } }];
}

// sort + merge
rows.sort((a,b) => (a.chunk_index||0) - (b.chunk_index||0));
const content = rows.map(r => String(r.text||'')).join('\n\n');

const first = rows[0] || {};

// try to read one resolver item if available, else fall back to rows
let resolved = {};
try {
  const r = $items('ResolveEpisode', 0, 0)?.[0]?.json;
  if (r) resolved = r;
} catch (_) {}

const file_id  = resolved.file_id  || first.file_id  || null;
const filename = resolved.filename || first.filename || '';

const episode_title  = normalizeEpisodeTitle(filename, filename);
const numMatch = (episode_title.match(/Episode\s+([0-9]{1,4})/i) || [])[1];
const episode_number = numMatch ? parseInt(numMatch, 10) : null;

return [{
  json: {
    episode_content: content.slice(0, 20000),
    episode_title,
    episode_number,
    file_id,
    filename,
    chunk_count: rows.length
  }
}];

// === RANK TOPICS: ROBUST PARSER + SCORER (handles non-JSON "### ..." inputs) ===

// 1) Get raw input safely
const first = $input.first();
const raw = String((first && first.json && (first.json.output ?? first.json.text)) || '').trim();

// 2) Try to parse JSON. If it's not JSON (e.g., starts with "### INSTAGRAM REEL"), fall back to extractor-from-text.
let concepts = [];
const stripFences = s => s.replace(/```json|```/g, '').trim();

const isLikelyJSON = (s) => {
  const t = s.trim().slice(0, 1);
  return t === '[' || t === '{';
};

try {
  if (isLikelyJSON(raw)) {
    const clean = stripFences(raw);
    const parsed = JSON.parse(clean);
    // Normalize to array of concept objects
    concepts = Array.isArray(parsed) ? parsed : (parsed.concepts || []);
  } else {
    // Fallback: extract concepts from non-JSON social post text with ### headers
    const text = raw.replace(/\r\n/g, '\n');

    const grab = (label) => {
      const re = new RegExp(`^###\\s*${label}\\s*\\n([\\s\\S]*?)(?=^###\\s*|\\Z)`, 'im');
      const m = text.match(re);
      return m ? m[1].trim() : '';
    };

    const ig = grab('INSTAGRAM REEL');
    const fb = grab('FACEBOOK POST');
    const li = grab('LINKEDIN POST');

    // Helper: split into candidate lines/sentences
    const toLines = (s) =>
      s
        .split('\n')
        .map(x => x.trim())
        .filter(Boolean)
        .flatMap(line => line.startsWith('- ') ? [line.slice(2)] : [line])
        .flatMap(line => line.split(/(?<=[.!?])\s+(?=[A-Z0-9])/)) // simple sentence split
        .map(x => x.trim())
        .filter(Boolean);

    const igLines = toLines(ig).filter(l => !l.startsWith('#'));
    const fbLines = toLines(fb);
    const liLines = toLines(li).map(l => l.replace(/^-\s*/, '').trim());

    // Derive titles from first few words
    const toTitle = (s) => (s.length > 70 ? s.slice(0, 67) + '…' : s);

    // Build concept objects (aim for 15–20 total)
    const buildConceptsFrom = (lines, tag) => {
      return lines.slice(0, 8).map(l => ({
        title: toTitle(l.replace(/https?:\/\/\S+/g, '').trim() || `${tag} Insight`),
        summary: l,
        evidence_snippet: l.slice(0, 200),
        tags: [tag, 'mindset', 'risk'].slice(0, 3),
        // Defaults to satisfy downstream scoring if your JSON normally includes these:
        actionability: 'medium',
        proof_type: /%|lower|reduce|increase|mitigat|data|metric/i.test(l) ? 'data' : (/framework|steps|tips|how/i.test(l) ? 'framework' : 'story'),
        platform_fit: 'all',
        hook_potential: 7
      }));
    };

    concepts = [
      ...buildConceptsFrom(igLines, 'instagram'),
      ...buildConceptsFrom(fbLines, 'facebook'),
      ...buildConceptsFrom(liLines, 'linkedin'),
    ];

    // If still < 15, backfill with hashtags or keywords
    if (concepts.length < 15) {
      const hash = (ig.match(/#[\w]+/g) || []).slice(0, 10);
      for (const h of hash) {
        concepts.push({
          title: `Hashtag angle: ${h}`,
          summary: `Talking point anchored on ${h}.`,
          evidence_snippet: h,
          tags: ['hashtag', h.replace('#','')],
          actionability: 'low',
          proof_type: 'story',
          platform_fit: 'all',
          hook_potential: 6
        });
        if (concepts.length >= 15) break;
      }
    }

    // Hard guarantee 15–20 by deduping and trimming
    const seen = new Set();
    concepts = concepts.filter(c => {
      const k = c.title.toLowerCase();
      if (seen.has(k)) return false;
      seen.add(k);
      return true;
    }).slice(0, 20);
  }
} catch (e) {
  throw new Error('Concept JSON parse fail: ' + e.message);
}

// 3) Scoring
const W = { a: 0.35, p: 0.25, r: 0.20, v: 0.15, e: 0.05 };

const pick = (obj, key, fallback) => (obj && obj[key] != null ? obj[key] : fallback);

const scored = concepts.map(c => {
  const actionability = String(pick(c, 'actionability', 'medium'));
  const proofType = String(pick(c, 'proof_type', 'story'));
  const platformFit = String(pick(c, 'platform_fit', 'all'));
  const hookPotential = Number(pick(c, 'hook_potential', 7)) || 7;

  const a = 7 + (actionability === 'high' ? 2 : actionability === 'medium' ? 1 : 0) + (proofType !== 'none' ? 1 : 0);
  const p = platformFit === 'all' ? 10 : 8;
  const r = proofType === 'data' ? 9 : (proofType === 'framework' ? 8 : 7);
  const v = Math.max(1, Math.min(10, hookPotential));
  const en = 7;

  const wt = a * W.a + p * W.p + r * W.r + v * W.v + en * W.e;
  return { 
    ...c, 
    scores: { weighted_total: Math.round(wt * 100) / 100 } 
  };
});

// 4) Select top 10 and enrich with episode metadata
let ep;
try {
  const arr = $items('Combine Episode Content', 0, 0);
  ep = (arr && arr[0] && arr[0].json) || {};
} catch (_) {
  ep = {};
}

const top = scored
  .sort((a, b) => b.scores.weighted_total - a.scores.weighted_total)
  .slice(0, 10);

return top.map((c, i) => ({
  day_number: i + 1,
  ...c,
  episode_title: ep.episode_title || $json?.episode_title || '',
  episode_number: ep.episode_number || $json?.episode_number || '',
  file_id: ep.file_id || $json?.file_id || ''
}));

// Build robust enrichment queries without "default" leaks or empty strings
const cRaw = ($json.concept ?? '').trim();
const epRaw = ($json.episode_title ?? '').trim();
const catRaw = ($json.category ?? '').toLowerCase().trim();

// Fallbacks that still read well if fields are missing
const topic = cRaw || epRaw || 'insurance agency growth';
const catWhitelist = ['sales_strategy','lead_gen','objection_handling','mindset','case_study'];
const category = catWhitelist.includes(catRaw) ? catRaw : (catRaw ? catRaw : 'mixed');

// Helper to clamp strings safely
const clamp = (s, n) => String(s || '').slice(0, n);

// Human-readable label for category intent inside strings
const catLabel = category === 'mixed'
  ? 'agency growth'
  : category.replace(/_/g, ' ');

// Core themed banks
const BANK = {
  sales_strategy: [
    `proven sales techniques for ${clamp(topic, 60)}`,
    `successful agent strategies in ${catLabel}`,
    `step-by-step sales framework for ${clamp(topic, 60)}`,
    `high-conversion discovery calls for ${clamp(topic, 60)}`,
    `pipeline hygiene and follow-up cadences for ${clamp(topic, 60)}`
  ],
  lead_gen: [
    `lead flow mechanics for ${clamp(topic, 60)}`,
    `Internet Lead Secrets prospecting tactics`,
    `Million Dollar Agency lead generation systems`,
    `inbound vs outbound mix for ${clamp(topic, 60)}`,
    `retention-driven referral loops for ${clamp(topic, 60)}`
  ],
  objection_handling: [
    `overcoming objections in ${clamp(topic, 60)}`,
    `objection handling scripts that convert`,
    `client resistance and closing techniques`,
    `price vs value reframes for ${clamp(topic, 60)}`,
    `risk transfer explanations clients understand`
  ],
  mindset: [
    `agent mindset for ${clamp(topic, 60)}`,
    `mental toughness for insurance pros`,
    `motivation and consistency systems`,
    `Chaos vs Order narrative to frame risk`,
    `habit stacks for daily production`
  ],
  case_study: [
    `agent success stories related to ${clamp(topic, 60)}`,
    `real results case studies (before/after)`,
    `proven strategies and measurable outcomes`,
    `field-tested playbooks for ${clamp(topic, 60)}`,
    `risk mitigation narratives clients share`
  ]
};

// If category is "mixed" or unknown, include **all themes**.
// Otherwise: include all themes + a few extras biased to the chosen category.
let queries = [];
if (category === 'mixed') {
  queries = [
    ...BANK.sales_strategy,
    ...BANK.lead_gen,
    ...BANK.objection_handling,
    ...BANK.mindset,
    ...BANK.case_study
  ];
} else {
  const extra = [
    `deep dive on ${catLabel} for ${clamp(topic, 60)}`,
    `playbooks: ${catLabel} → ${clamp(topic, 60)}`,
    `quick wins in ${catLabel} for busy agents`
  ];
  queries = [
    ...BANK.sales_strategy,
    ...BANK.lead_gen,
    ...BANK.objection_handling,
    ...BANK.mindset,
    ...BANK.case_study,
    ...extra
  ];
}

// Clean up: dedupe, strip empties, trim, clamp length for embedding safety
const seen = new Set();
const enrichment_queries = queries
  .map(q => (q || '').toString().trim())
  .filter(q => q.length > 0)
  .map(q => clamp(q, 200)) // keep each under ~200 chars for embeddings
  .filter(q => {
    const k = q.toLowerCase();
    if (seen.has(k)) return false;
    seen.add(k);
    return true;
  });

// Absolute fallback to avoid empty array in edge cases
if (enrichment_queries.length === 0) {
  enrichment_queries.push(
    `foundational strategies for ${clamp(topic, 60)}`
  );
}

return {
  ...$json,
  enrichment_queries
};

