 function decodeHtml(str) {
   if (!str) return '';
   var s = String(str);
   s = s.replace(/&nbsp;/g, ' ')
        .replace(/&amp;/g, '&')
        .replace(/&lt;/g, '<')
        .replace(/&gt;/g, '>')
        .replace(/&quot;/g, '"')
        .replace(/&apos;/g, "'")
        .replace(/&#(\d+);/g, function(_, d){
          var n = parseInt(d,10);
          return isFinite(n) ? String.fromCharCode(n) : _;
        })
        .replace(/&#x([0-9a-fA-F]+);/g, function(_, h){
          var n = parseInt(h,16);
          return isFinite(n) ? String.fromCharCode(n) : _;
        });
   return s;
 }

 function stripTags(str) {
   if (!str) return '';
   return String(str)
       .replace(/<[^>]+>/g, ' ')
       .replace(/[\/]?[a-z]+>/gi, ' ');
 }

 function tighten(str) {
   return String(str)
       .replace(/[ \t]+/g, ' ')
       .replace(/\s*\n\s*/g, '\n')
       .replace(/\n{3,}/g, '\n\n')
       .trim();
 }

 function denoise(str) {
   var lines = String(str).split(/\r?\n/).map(function(l){ return l.trim(); });
   var seen = new Set();
   var out = [];

   for (var i = 0; i < lines.length; i++) {
     var raw = lines[i];
     if (!raw) continue;
     if (/^[-=]{3,}/.test(raw)) continue;
     if (/^\d{1,2}:\d{2}(:\d{2})?$/.test(raw)) continue;
     if (/^speaker\s*\d*[:.\\-]?\s*$/i.test(raw)) continue;
     if (/^[^\w]+$/.test(raw)) continue;
     if (/^(cite|time|p|te|\/time)\s*:?$/i.test(raw)) continue;

     raw = raw.replace(/^\s*speaker\s*\d*\s*[:.\\-]\s*/i, '');
     var norm = raw.toLowerCase().replace(/[^a-z0-9\s]/g, '').replace(/\s+/g, ' ').trim();

     if (norm.length < 4) continue;
     if (seen.has(norm)) continue;

     seen.add(norm);
     out.push(raw);
   }

   return out.join('\n');
 }

 function cleanTitle(title, content) {
   var t = (title || '').toString().trim();
   var first = (content || '').toString().split(/\r?\n/).find(Boolean) || '';
   var re = /(?:^|\b)(?:ep(?:isode)?|episode)\s*[:#-]\s([0-9]{1,4})/i;

   var m = t.match(re);
   if (m) return 'Episode ' + m[1];

   m = first.match(re);
   if (m) return 'Episode ' + m[1];

   return t || 'Episode';
 }

