const approved = ['APPROVE','APPROVE_WITH_EDITS'].includes(rec);
return [{ json: { ...$json, review: $json, recommendation: rec, approved } }];

const hasAll = ['instagram_content','facebook_content','linkedin_content'].every(k => ($json[k]||'').trim());
if (!hasAll) throw new Error('One or more sections empty before review routing.');
return [{json:$json}];

// Init Context (place after Combine Enrichment Data)
const crypto = require('crypto');
const file_id  = $json.file_id || 'non-episode';
const concept  = $json.concept || $json.topic_title || 'untitled';
const episode  = $json.episode_number || '';
const work_id  = crypto.createHash('sha256').update(JSON.stringify({file_id,concept,episode})).digest('hex');
const attempt  = Number.isFinite($json.attempt) ? $json.attempt : 0;
return [{ json: { ...$json, work_id, attempt } }];

// Increment Attempt (Route C)
const nextAttempt = (Number($json.attempt) || 0) + 1;
return [{ json: { ...$json, attempt: nextAttempt } }];

const apply = (fixes, txt) => {
  if (!Array.isArray(fixes) || !txt) return txt || '';
  let out = String(txt);
  for (const f of fixes) {
    const from = (f?.current || '').trim();
    const to   = (f?.fix || '').trim();
    if (!from || !to) continue;
    if (!out.includes(from)) continue;
    out = out.replace(from, to);
  }
  return out;
};

return [{
  json: {
    ...$json,
    instagram_content: apply($json.review?.instagram_review?.required_fixes, $json.instagram_content),
    facebook_content:  apply($json.review?.facebook_review?.required_fixes,  $json.facebook_content),
    linkedin_content:  apply($json.review?.linkedin_review?.required_fixes,  $json.linkedin_content)
  }
}];

// Parse 3 Sections (Local) — tight version
// INPUT accepted on this item: { strict_output? | output? | text? | content? }
// OUTPUT: { instagram_content, facebook_content, linkedin_content, strict_output, day_number?, episode_title?, topic_title? }

// ---------- helpers ----------
const prefer = (...vals) => {
  for (const v of vals) if (v != null && String(v).trim() !== '') return v;
  return '';
};

const clean = (s) => {
  let t = String(s ?? '');
  return t
    .replace(/\r\n/g, '\n')
    .replace(/[\u200B\u200C\u200D\uFEFF]/g, '')
    .replace(/\t/g, '  ')
    .replace(/```[\s\S]*?```/g, m => m.replace(/```/g,''))   // code fences
    .replace(/`{1,3}([^`]+)`{1,3}/g, '$1')                   // inline code
    .replace(/^\s*#{1,6}\s*/gm, '')                          // md headers
    .replace(/^\s*[-*•·]+[\t ]*/gm, '')                      // bullets
    .replace(/[•·]/g, '')                                    // stray bullets
    .replace(/\*\*(.*?)\*\*/g, '$1').replace(/__(.*?)__/g, '$1').replace(/_(.*?)_/g, '$1') // bold/italics
    .replace(/--+/g, '—')                                    // double dashes
    .replace(/\[([^\]]+)\]\((https?:\/\/[^\s)]+)\)/g, '$1 ($2)') // [text](url) -> text (url)
    .replace(/(^|\s)#{2,}(?=\s|$)/g, '$1')                   // remove bare ## clusters
    .replace(/\s+([.,!?;:])(?!\w)/g, '$1')                   // space before punct
    .replace(/[ \t]+\n/g, '\n')                              // trim line-end spaces
    .replace(/\n{3,}/g, '\n\n')                              // collapse 3+ newlines
    .replace(/[^\S\r\n]{2,}/g, ' ')                          // collapse spaces
    .trim();
};

const getSection = (src, label) => {
  const re = new RegExp(String.raw`^[\t ]*###\s*${label}\s*:?\s*\n?([\s\S]*?)(?=^[\t ]*###\s*|\Z)`,'im');
  const m  = src.match(re);
  return m ? m[1].trim() : '';
};

const fallbackBlock = (src, labelRegex) => {
  const re = new RegExp(String.raw`^\s*${labelRegex}\s*:\s*`, 'im');
  if (!re.test(src)) return '';
  const after = src.split(re)[1] || '';
  const next  = after.search(/^[\t ]*###\s*/m);
  return (next >= 0 ? after.slice(0, next) : after).trim();
};

// ---------- 1) read input text ----------
let rawText = String(
  prefer($json.strict_output, $json.output, $json.text, $json.content)
).replace(/\r\n/g, '\n').replace(/[\u200B\u200C\u200D\uFEFF]/g, '').trim();

if (!rawText) throw new Error('Parse3Sections: no input text found');

// ---------- 2) extract sections (with fallbacks) ----------
let ig = getSection(rawText, 'INSTAGRAM\\s*REEL');
let fb = getSection(rawText, 'FACEBOOK\\s*POST');
let li = getSection(rawText, 'LINKEDIN\\s*POST');

if (!ig) ig = fallbackBlock(rawText, 'Instagram\\s*Reel');
if (!fb) fb = fallbackBlock(rawText, 'Facebook');
if (!li) li = fallbackBlock(rawText, 'LinkedIn');

// ---------- 3) clean ----------
ig = clean(ig);
fb = clean(fb);
li = clean(li);

// ---------- 4) validate ----------
const empty = [];
if (!ig) empty.push('INSTAGRAM REEL');
if (!fb) empty.push('FACEBOOK POST');
if (!li) empty.push('LINKEDIN POST');
if (empty.length) throw new Error('Parse3Sections: empty sections → ' + empty.join(', '));

// ---------- 5) rebuild canonical strict_output ----------
const strict_output =
  '### INSTAGRAM REEL\n' + ig + '\n\n' +
  '### FACEBOOK POST\n' + fb + '\n\n' +
  '### LINKEDIN POST\n' + li;

// ---------- 6) pass through light meta if present ----------
const out = {
  instagram_content: ig,
  facebook_content:  fb,
  linkedin_content:  li,
  strict_output
};
for (const k of ['day_number','episode_title','topic_title']) {
  if ($json[k] != null) out[k] = $json[k];
}

return [{ json: out }];

const items = $input.all();
return items.map(item => {
  return {
    json: {
      ...item.json,
      generated_content: item.json.output || item.json, // Adjust based on your node output
      from_cache: false,
      cache_hit_count: 0
    }
  };
});

return [{
  json: {
    review: $json,
    recommendation: $json.overall_recommendation || 'REJECT'
  }
}];

// Pass review_status so setPromptVersion knows to use v2
  return [{
    json: {
      ...$json,
      review_status: 'REJECT',  // ← Triggers v2 in setPromptVersion
      attempt: $json.attempt     // ← Keep the incremented attempt count
    }
  }];

